import React, { useEffect } from 'react';
import { useLocation } from 'react-router-dom';
import ReactPlayer from 'react-player';
import { useResultContext } from '../contexts/ResultContextProvider';
import Loading from './Loading';

const Results = () => {
  const { results, isLoading, getResults, searchTerm } = useResultContext();
  const location = useLocation();

  useEffect(() => {
    if (searchTerm) {
      getResults(`${location.pathname}/q=${searchTerm}`);
    }
  }, [searchTerm, location.pathname]);

  if (isLoading) {
    return <Loading />;
  }
  switch (location.pathname) {
    case '/search':
      return (
        <div className='flex flex-wrap justify-between space-y-6 sm:p-56'>
          {results?.results?.map(({ link, title }, i) => (
            <div key={i} className='w-full md:w-2/5 '>
              <a href={link} target='_blank' rel='noreferrer'>
                <p className='text-sm '>
                  {link.length > 30 ? `${link.substring(0, 30)}...` : link}
                </p>
                <p className='text-lg text-blue-700 hover:underline dark:text-blue-300 '>
                  {title}
                </p>
              </a>
            </div>
          ))}
        </div>
      );

    case '/news':
      return (
        <div className='flex flex-wrap items-center justify-between space-y-6 sm:p-56'>
          {/* { JSON.stringify(results.entries[4].links[0])} */}
          {/* {results?.entries?.map((item,i)=>(
             <div key={i}>{JSON.stringify(item)}</div>
           ))} */}
          {results?.entries?.map((item,i)=> (
            <div key={item?.id} className='w-full md:w-2/5 '>
              <a
                href={item?.links?.[0].href}
                target='_blank'
                rel='noreferrer'
                className='hover:underline'
              >
                <p className='text-lg text-blue-700 dark:text-blue-300 '>
                  {item?.title}
                </p>
                <div className='flex gap-4'>
                  <a href={item?.source?.href} target='_blank' rel='noreferrer'>
                    {item?.source?.href}
                  </a>
                </div>
              </a>
            </div>
          ))}
        </div>
      );

    case '/image':
      return (
        <div className='flex flex-wrap items-center justify-center'>
          {results?.image_results?.map(
            ({ image: { alt, src }, link: { href, title } }, i) => (
              <a
                key={i}
                href={href}
                target='_blank'
                rel='noreferrer'
                className='p-5 sm:p-3 '
              >
                <img src={src} alt={title} loading='lazy' />
                <p className='mt-2 break-words w-37 text-small '>{title}</p>
              </a>
            )
          )}
        </div>
      );

    case '/video':
      return 'videos';


    default:
      return 'ERROR!';
  }
};

export default Results;
