import React, { useEffect } from 'react';
import { useLocation } from 'react-router-dom';
import ReactPlayer from 'react-player';
import { useResultContext } from '../contexts/ResultContextProvider';
import Loading from './Loading';

const Results = () => {
  const { results, isLoading, getResults, searchTerm } = useResultContext();
  const location = useLocation();

  useEffect(() => {
    if (searchTerm) {
      if (location.path ==='/video'){
        getResults(`/video/q=${searchTerm}`);
      }
      if (location.path ==='/image'){
        getResults(`/image/q=${searchTerm}`);
      }
      if (location.path ==='/news'){
        getResults(`/news/q=${searchTerm}`);
      }
    }

  }, []);

  if (isLoading) {
    return <Loading />;
  } else {
    switch (location.pathname) {
      case '/search':
        return (
          <div className='flex flex-wrap justify-between space-y-6 sm:p-56'>
            {results?.results?.map(({ link, title }, i) => (
              <div key={i} className='w-full md:w-2/5 '>
                <a href={link} target='_blank' rel='noreferrer'>
                  <p className='text-sm '>
                    {link.length > 30 ? `${link.substring(0, 30)}...` : link}
                  </p>
                  <p className='text-lg text-blue-700 hover:underline dark:text-blue-300 '>
                    {title}
                  </p>
                </a>
              </div>
            ))}
          </div>
        );
        break;
      case '/news':
        return 'news';
        break;
      case '/image':
        return (
          <div className='flex flex-wrap items-center justify-center'>
            {results?.image_results?.map(
              ({ image:{alt,src}, link: { href, title } }, i) => (
                <a key={i} href={href} target='_blank' rel='noreferrer' className='p-5 sm:p-3 '>
                  <img src={src} alt={title} loading="lazy"/>
                  <p className='mt-2 break-words w-37 text-small '>{title}</p>
                </a>
              )
            )}
          </div>
        );
        break;
      case '/video':
        return 'videos';
        break;

      default:
        return 'ERROR!';
    }
  }
};

export default Results;
