import React, { useEffect } from 'react';
import { useLocation } from 'react-router-dom';
import ReactPlayer from 'react-player';
import { useResultContext } from '../contexts/ResultContextProvider';
import Loading from './Loading';

const Results = () => {
  const { results, isLoading, getResults, searchTerm } = useResultContext();
  const location = useLocation();

  // useEffect(() => {
  //   getResults('/search/q=react for beginners');
  // }, []);
  // if (isLoading) {
  //   <Loading />;
  // }
  console.log(location.pathname);
  switch (location.pathname) {
    case '/search':
      return (
        <div className='flex flex-wrap justify-between space-y-6 sm:p-56'>
          {results?.results?.map(({link,title},i)=>(
            <div key={i} className="w-full md:w-2/5 ">
              <a href={link} target="_blank" rel="noreferrer">
                <p className="text-sm ">
                  {link.length>30 ?`${link.substring(0,30)}...`:link}
                </p>
                <p className="text-lg text-blue-700 hover:underline dark:text-blue-300 ">
                  {title}
                </p>
              </a>
            </div>
          ))}
        </div>
      );
      break;
    case '/news':
      return 'news';
      break;
    case '/images':
      return 'images';
      break;
    case '/videos':
      return 'videos';
      break;

    default:
      return 'ERROR!';
  }
};

export default Results;
